INSERT INTO `articles_content` VALUES(37, 32, 'HƯỚNG VỀ CỘI NGUỒN – PHÁT HUY TRUYỀN THỐNG*', NULL, '<p class=\"MsoNormal\" style=\"text-align: center;\"><span style=\"font-size: 14.0pt; line-height: 107%;\">Trịnh Gia truyền thống bao đời</span></p>\r\n<p class=\"MsoNormal\" style=\"text-align: center;\"><span style=\"font-size: 14.0pt; line-height: 107%;\">Lưu truyền thế phả s&aacute;ng ngời gia phong</span></p>\r\n<p class=\"MsoNormal\" style=\"text-align: center;\"><span style=\"font-size: 14.0pt; line-height: 107%;\">Từ thời ph&aacute;t b&atilde;i ,khơi s&ocirc;ng</span></p>\r\n<p class=\"MsoNormal\" style=\"text-align: center;\"><span style=\"font-size: 14.0pt; line-height: 107%;\">Đất hoang, khai khẩn<span style=\"color: red;\">, lập l&agrave;ng </span>định cư</span></p>\r\n<p class=\"MsoNormal\" style=\"text-align: center;\"><span style=\"font-size: 14.0pt; line-height: 107%;\">Mấy <span style=\"color: red;\">trăm</span><span style=\"mso-spacerun: yes;\"> </span>năm<span style=\"mso-spacerun: yes;\"> </span>lẻ c&oacute; dư</span></p>\r\n<p class=\"MsoNormal\" style=\"text-align: center;\"><span style=\"font-size: 14.0pt; line-height: 107%;\">Nối d&ograve;ng hai chục, c&oacute; thừa vẻ vang.</span></p>\r\n<p class=\"MsoNormal\" style=\"text-align: center;\"><span style=\"font-size: 14.0pt; line-height: 107%;\">&nbsp;</span></p>\r\n<p class=\"MsoNormal\" style=\"text-align: center;\"><span style=\"font-size: 14.0pt; line-height: 107%;\">Y&ecirc;n Li&ecirc;u Thượng, gốc tổ l&agrave;ng</span></p>\r\n<p class=\"MsoNormal\" style=\"text-align: center;\"><span style=\"font-size: 14.0pt; line-height: 107%;\">X&oacute;m Ch&ugrave;a l&agrave; gốc Từ đường tiền nh&acirc;n</span></p>\r\n<p class=\"MsoNormal\" style=\"text-align: center;\"><span style=\"font-size: 14.0pt; line-height: 107%;\">Vốn <span style=\"color: red;\">l&agrave;</span> thuần t&uacute;y N&ocirc;ng d&acirc;n</span></p>\r\n<p class=\"MsoNormal\" style=\"text-align: center;\"><span style=\"font-size: 14.0pt; line-height: 107%;\">Nếp nh&agrave;, <span style=\"color: red;\">Hương</span> ước, chuy&ecirc;n cần tăng gia</span></p>\r\n<p class=\"MsoNormal\" style=\"text-align: center;\"><span style=\"font-size: 14.0pt; line-height: 107%;\">T&iacute;nh t&igrave;nh thuần,ph&aacute;c, khoan, h&ograve;a</span></p>\r\n<p class=\"MsoNormal\" style=\"text-align: center;\"><span style=\"font-size: 14.0pt; line-height: 107%;\">Ch&acirc;n t&igrave;nh, mộc mạc xa hoa kh&ocirc;ng hề.</span></p>\r\n<p class=\"MsoNormal\" style=\"text-align: center;\"><span style=\"font-size: 14.0pt; line-height: 107%;\">&nbsp;</span></p>\r\n<p class=\"MsoNormal\" style=\"text-align: center;\"><span style=\"font-size: 14.0pt; line-height: 107%;\">Đ&agrave;o giếng nước, đắp bờ đ&ecirc;</span></p>\r\n<p class=\"MsoNormal\" style=\"text-align: center;\"><span style=\"font-size: 14.0pt; line-height: 107%;\">Tảo tần lam lũ, chuy&ecirc;n nghề n&ocirc;ng trang</span></p>\r\n<p class=\"MsoNormal\" style=\"text-align: center;\"><span style=\"font-size: 14.0pt; line-height: 107%;\">H&ograve;a vui trong x&oacute;m, ngo&agrave;i l&agrave;ng</span></p>\r\n<p class=\"MsoNormal\" style=\"text-align: center;\"><span style=\"font-size: 14.0pt; line-height: 107%;\">Xu&acirc;n thu ca h&aacute;t chẳng m&agrave;ng ngo&agrave;i tr&ocirc;ng</span></p>\r\n<p class=\"MsoNormal\" style=\"text-align: center;\"><span style=\"font-size: 14.0pt; line-height: 107%;\">Một m&aacute;i rạ thủa Tổ t&ocirc;ng</span></p>\r\n<p class=\"MsoNormal\" style=\"text-align: center;\"><span style=\"font-size: 14.0pt; line-height: 107%;\">Nay n&ecirc;n trăm nếp ng&oacute;i hồng tường x&acirc;y.</span></p>\r\n<p class=\"MsoNormal\" style=\"text-align: center;\"><span style=\"font-size: 14.0pt; line-height: 107%;\">&nbsp;</span></p>\r\n<p class=\"MsoNormal\" style=\"text-align: center;\"><span style=\"font-size: 14.0pt; line-height: 107%;\">Ch&aacute;u con giang rộng c&aacute;nh bay</span></p>\r\n<p class=\"MsoNormal\" style=\"text-align: center;\"><span style=\"font-size: 14.0pt; line-height: 107%;\">V&agrave;o Nam, ra Bắc, đ&ocirc;ng t&acirc;y khắp miền</span></p>\r\n<p class=\"MsoNormal\" style=\"text-align: center;\"><span style=\"font-size: 14.0pt; line-height: 107%;\">Dẫu đi mu&ocirc;n dặm<span style=\"mso-spacerun: yes;\">&nbsp; </span><span style=\"color: red;\">mu&ocirc;n tr&ugrave;ng</span></span></p>\r\n<p class=\"MsoNormal\" style=\"text-align: center;\"><span style=\"font-size: 14.0pt; line-height: 107%;\">Vẫn về đất tổ , gốc L&agrave;ng nơi đ&acirc;y</span></p>\r\n<p class=\"MsoNormal\" style=\"text-align: center;\"><span style=\"font-size: 14.0pt; line-height: 107%;\">D&ugrave; cho hạnh ph&uacute;c đủ đầy</span></p>\r\n<p class=\"MsoNormal\" style=\"text-align: center;\"><span style=\"font-size: 14.0pt; line-height: 107%;\">Vẫn lu&ocirc;n đ&oacute;ng g&oacute;p, chung x&acirc;y phố phường.</span></p>\r\n<p class=\"MsoNormal\" style=\"text-align: center;\"><span style=\"font-size: 14.0pt; line-height: 107%;\">&nbsp;</span></p>\r\n<p class=\"MsoNormal\" style=\"text-align: center;\"><span style=\"font-size: 14.0pt; line-height: 107%;\">Học h&agrave;nh tr&iacute; thức mở mang</span></p>\r\n<p class=\"MsoNormal\" style=\"text-align: center;\"><span style=\"font-size: 14.0pt; line-height: 107%;\">Khuyến t&agrave;i, khuyến học mở đường vinh quang</span></p>\r\n<p class=\"MsoNormal\" style=\"text-align: center;\"><span style=\"font-size: 14.0pt; line-height: 107%;\">Cuộc chiến gữi nước, giữ l&agrave;ng</span></p>\r\n<p class=\"MsoNormal\" style=\"text-align: center;\"><span style=\"font-size: 14.0pt; line-height: 107%;\">H&agrave;ng trăm con ,ch&aacute;u l&ecirc;n đường t&ograve;ng qu&acirc;n</span></p>\r\n<p class=\"MsoNormal\" style=\"text-align: center;\"><span style=\"font-size: 14.0pt; line-height: 107%;\">Chiến trường ng&atilde; xuống bao lần</span></p>\r\n<p class=\"MsoNormal\" style=\"text-align: center;\"><span style=\"font-size: 14.0pt; line-height: 107%;\">Ng&agrave;y vui đại thắng dự phần vẻ vang.</span></p>\r\n<p class=\"MsoNormal\" style=\"text-align: center;\"><span style=\"font-size: 14.0pt; line-height: 107%;\">&nbsp;</span></p>\r\n<p class=\"MsoNormal\" style=\"text-align: center;\"><span style=\"font-size: 14.0pt; line-height: 107%;\">Người về x&acirc;y dựng qu&ecirc; hương</span></p>\r\n<p class=\"MsoNormal\" style=\"text-align: center;\"><span style=\"font-size: 14.0pt; line-height: 107%;\">Th&acirc;m canh, tăng vụ,mở mang ngh&agrave;nh nghề</span></p>\r\n<p class=\"MsoNormal\" style=\"text-align: center;\"><span style=\"font-size: 14.0pt; line-height: 107%;\">Đổi mới nếp sống l&agrave;ng qu&ecirc;</span></p>\r\n<p class=\"MsoNormal\" style=\"text-align: center;\"><span style=\"font-size: 14.0pt; line-height: 107%;\">Văn minh, tiến bộ, bỏ lề dị đoan</span></p>\r\n<p class=\"MsoNormal\" style=\"text-align: center;\"><span style=\"font-size: 14.0pt; line-height: 107%;\">Th&ocirc;i rồi b&oacute;i to&aacute;n hoang mang</span></p>\r\n<p class=\"MsoNormal\" style=\"text-align: center;\"><span style=\"font-size: 14.0pt; line-height: 107%;\">Xa l&igrave;a <span style=\"color: red;\">đ&oacute;i kh&aacute;t, khổ đau </span>ng&agrave;y n&agrave;o.</span></p>\r\n<p class=\"MsoNormal\" style=\"text-align: center;\"><span style=\"font-size: 14.0pt; line-height: 107%;\">&nbsp;</span></p>\r\n<p class=\"MsoNormal\" style=\"text-align: center;\"><span style=\"font-size: 14.0pt; line-height: 107%;\">Nhịp sống ng&agrave;y một n&acirc;ng cao</span></p>\r\n<p class=\"MsoNormal\" style=\"text-align: center;\"><span style=\"font-size: 14.0pt; line-height: 107%;\">T&igrave;nh nghĩa dồi d&agrave;o, đầm ấp qu&ecirc; hương</span></p>\r\n<p class=\"MsoNormal\" style=\"text-align: center;\"><span style=\"font-size: 14.0pt; line-height: 107%;\">Đường <span style=\"color: red;\">l&agrave;ng, ng&otilde; x&oacute;m </span>khang trang</span></p>\r\n<p class=\"MsoNormal\" style=\"text-align: center;\"><span style=\"font-size: 14.0pt; line-height: 107%;\">B&ecirc; t&ocirc;ng, dải nhựa, rộn r&agrave;ng xe qua</span></p>\r\n<p class=\"MsoNormal\" style=\"text-align: center;\"><span style=\"font-size: 14.0pt; line-height: 107%;\">Đọc b&agrave;i tiếng trẻ vang xa</span></p>\r\n<p class=\"MsoNormal\" style=\"text-align: center;\"><span style=\"font-size: 14.0pt; line-height: 107%;\">&Ecirc;m ru tiếng h&aacute;t ng&acirc;n nga l&ograve;ng người.</span></p>\r\n<p class=\"MsoNormal\" style=\"text-align: center;\"><span style=\"font-size: 14.0pt; line-height: 107%;\">&nbsp;</span></p>\r\n<p class=\"MsoNormal\" style=\"text-align: center;\"><span style=\"font-size: 14.0pt; line-height: 107%;\">&Aacute;nh s&aacute;ng tr&agrave;n ngập trong ngo&agrave;i</span></p>\r\n<p class=\"MsoNormal\" style=\"text-align: center;\"><span style=\"font-size: 14.0pt; line-height: 107%;\">Quy&eacute;t sạch u ho&agrave;i, thủ tục m&ecirc; si</span></p>\r\n<p class=\"MsoNormal\" style=\"text-align: center;\"><span style=\"font-size: 14.0pt; line-height: 107%;\"><span style=\"mso-spacerun: yes;\">&nbsp;</span>Từ đường k&iacute;nh lễ định kỳ</span></p>\r\n<p class=\"MsoNormal\" style=\"text-align: center;\"><span style=\"font-size: 14.0pt; line-height: 107%;\">Hương hoa tinh khiết,<span style=\"color: red;\"> lễ </span>nghi t&acirc;m th&agrave;nh</span></p>\r\n<p class=\"MsoNormal\" style=\"text-align: center;\"><span style=\"font-size: 14.0pt; line-height: 107%;\">Rằm, Xu&acirc;n, Đ&ocirc;ng tr&iacute;, Thanh minh</span></p>\r\n<p class=\"MsoNormal\" style=\"text-align: center;\"><span style=\"font-size: 14.0pt; line-height: 107%;\">Nghi lễ đuỳnh huỳnh, lễ nhạc nghi&ecirc;m trang.</span></p>\r\n<p class=\"MsoNormal\" style=\"text-align: center;\"><span style=\"font-size: 14.0pt; line-height: 107%;\">&nbsp;</span></p>\r\n<p class=\"MsoNormal\" style=\"text-align: center;\"><span style=\"font-size: 14.0pt; line-height: 107%;\">T&ocirc;n ti, trật tự ph&acirc;n h&agrave;ng</span></p>\r\n<p class=\"MsoNormal\" style=\"text-align: center;\"><span style=\"font-size: 14.0pt; line-height: 107%;\">Dưới h&ograve;a, tr&ecirc;n thuận r&otilde; r&agrave;ng, ph&acirc;n minh</span></p>\r\n<p class=\"MsoNormal\" style=\"text-align: center;\"><span style=\"font-size: 14.0pt; line-height: 107%;\">Ở ăn c&oacute; nghĩa, c&oacute; t&igrave;nh</span></p>\r\n<p class=\"MsoNormal\" style=\"text-align: center;\"><span style=\"font-size: 14.0pt; line-height: 107%;\">Buồn vui, chia sẻ, r&aacute;ch l&agrave;nh bao dung</span></p>\r\n<p class=\"MsoNormal\" style=\"text-align: center;\"><span style=\"font-size: 14.0pt; line-height: 107%;\">Cha gi&agrave;, nh&agrave; n&aacute;t lo chung</span></p>\r\n<p class=\"MsoNormal\" style=\"text-align: center;\"><span style=\"font-size: 14.0pt; line-height: 107%;\">Kh&ocirc;ng b&agrave;y ăn uống tiệc t&ugrave;ng xa hoa.</span></p>\r\n<p class=\"MsoNormal\" style=\"text-align: center;\"><span style=\"font-size: 14.0pt; line-height: 107%;\">&nbsp;</span></p>\r\n<p class=\"MsoNormal\" style=\"text-align: center;\"><span style=\"font-size: 14.0pt; line-height: 107%;\">Đường tu k&iacute;nh Phật tại gia</span></p>\r\n<p class=\"MsoNormal\" style=\"text-align: center;\"><span style=\"font-size: 14.0pt; line-height: 107%;\">Thiện t&acirc;m l&agrave; Phật, <span style=\"color: red;\">mọi người </span>t&igrave;m đ&acirc;u?</span></p>\r\n<p class=\"MsoNormal\" style=\"text-align: center;\"><span style=\"font-size: 14.0pt; line-height: 107%;\">Tr&aacute;nh xa t&agrave; khuyết hiểm s&acirc;u</span></p>\r\n<p class=\"MsoNormal\" style=\"text-align: center;\"><span style=\"font-size: 14.0pt; line-height: 107%;\">Đặt b&agrave;y lắm mối, cơ cầu lăng nhăng</span></p>\r\n<p class=\"MsoNormal\" style=\"text-align: center;\"><span style=\"font-size: 14.0pt; line-height: 107%;\">Ch&acirc;n l&yacute; tu&acirc;n thủ cận năng</span></p>\r\n<p class=\"MsoNormal\" style=\"text-align: center;\"><span style=\"font-size: 14.0pt; line-height: 107%;\">Dẫu qua ghềnh th&aacute;c chẳng văng tay ch&egrave;o.</span></p>\r\n<p class=\"MsoNormal\" style=\"text-align: center;\">&nbsp;</p>\r\n<p class=\"MsoNormal\" style=\"text-align: center;\"><span style=\"font-size: 14.0pt; line-height: 107%;\">Khuyến học, giải thưởng đ&atilde; treo</span></p>\r\n<p class=\"MsoNormal\" style=\"text-align: center;\"><span style=\"font-size: 14.0pt; line-height: 107%;\">D&agrave;nh người tr&uacute;ng tuyển ghi v&agrave;o bảng son</span></p>\r\n<p class=\"MsoNormal\" style=\"text-align: center;\"><span style=\"font-size: 14.0pt; line-height: 107%;\">Nhiệm vụ cứu nước đ&atilde; tr&ograve;n</span></p>\r\n<p class=\"MsoNormal\" style=\"text-align: center;\"><span style=\"font-size: 14.0pt; line-height: 107%;\">Nhiệm vụ x&acirc;y dựng nước non đang chờ</span></p>\r\n<p class=\"MsoNormal\" style=\"text-align: center;\"><span style=\"font-size: 14.0pt; line-height: 107%;\">&ldquo; Hiền t&agrave;i nguy&ecirc;n kh&iacute; Quốc Gia&rdquo;</span></p>\r\n<p class=\"MsoNormal\" style=\"text-align: center;\"><span style=\"font-size: 14.0pt; line-height: 107%;\">Gi&uacute;p nước, gi&uacute;p nh&agrave; rạng rỡ thanh danh.</span></p>\r\n<p class=\"MsoNormal\" style=\"text-align: center;\"><span style=\"font-size: 14.0pt; line-height: 107%;\">&nbsp;</span></p>\r\n<p class=\"MsoNormal\" style=\"text-align: center;\"><span style=\"font-size: 14.0pt; line-height: 107%;\">Tiến l&ecirc;n tiếp bước cha anh</span></p>\r\n<p class=\"MsoNormal\" style=\"text-align: center;\"><span style=\"font-size: 14.0pt; line-height: 107%;\">Gắng sức học h&agrave;nh, đạt tới đỉnh cao</span></p>\r\n<p class=\"MsoNormal\" style=\"text-align: center;\"><span style=\"font-size: 14.0pt; line-height: 107%;\">Sao cho xứng mặt anh h&agrave;o</span></p>\r\n<p class=\"MsoNormal\" style=\"text-align: center;\"><span style=\"font-size: 14.0pt; line-height: 107%;\">Quyết t&acirc;m trời cũng thuận theo l&ograve;ng người</span></p>\r\n<p class=\"MsoNormal\" style=\"text-align: center;\"><span style=\"font-size: 14.0pt; line-height: 107%;\">Hạnh Ph&uacute;c mở rộng ch&acirc;n trời</span></p>\r\n<p class=\"MsoNormal\" style=\"text-align: center;\"><span style=\"font-size: 14.0pt; line-height: 107%;\">Ph&aacute;t huy truyền thống, <span style=\"color: red;\">s&aacute;ng ngời tương lai .</span></span></p>\r\n<p class=\"MsoNormal\" style=\"text-align: center;\"><strong style=\"mso-bidi-font-weight: normal;\"><span style=\"font-size: 14.0pt; line-height: 107%;\"><span style=\"mso-spacerun: yes;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span><span style=\"mso-spacerun: yes;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span><span style=\"mso-spacerun: yes;\">&nbsp;&nbsp;&nbsp;</span>Trịnh Đ&igrave;nh Điềm </span></strong></p>\r\n<p class=\"MsoListParagraph\" style=\"text-indent: -0.25in; text-align: center;\"><!-- [if !supportLists]--><span style=\"font-family: Symbol; mso-fareast-font-family: Symbol; mso-bidi-font-family: Symbol;\"><span style=\"mso-list: Ignore;\">&middot;<span style=\"font: 7.0pt \'Times New Roman\';\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span></span></span><!--[endif]--><em style=\"mso-bidi-font-style: normal;\">Mạn ph&eacute;p t&aacute;c giả, Ban bi&ecirc;n tập c&oacute; chỉnh sữa một số từ (chữ mầu đỏ) cho c&oacute; vần v&agrave; r&otilde; nghĩa hơn.</em></p>\r\n<p class=\"MsoListParagraph\" style=\"text-indent: -.25in; mso-list: l0 level1 lfo1;\"><em style=\"mso-bidi-font-style: normal;\"><img style=\"display: block; margin-left: auto; margin-right: auto;\" src=\"http://cdn1506.cdn4s4.io.vn/media/gioi-thieu/hinh-anh-nha-tho-hien-nay-2.jpg\" alt=\"\" width=\"800\" height=\"600\"></em></p>', 'HƯỚNG VỀ CỘI NGUỒN – PHÁT HUY TRUYỀN THỐNG*', NULL, NULL, 'huong ve coi nguon phat huy truyen thong', 'vi');
