INSERT INTO `articles_content` VALUES(56, 51, 'Hướng dẫn sử dụng', '<p>Sau một thời gian nỗ lực của c&aacute;c th&agrave;nh vi&ecirc;n Ban bi&ecirc;n tập c&ugrave;ng với sự phối hợp chặt chẽ với C&ocirc;ng Ty TNHH phần mền Nh&acirc;n H&ograve;a, Ban bi&ecirc;n tập đ&atilde; ho&agrave;n th&agrave;nh to&agrave;n bộ trang Website &ldquo; <strong style=\"mso-bidi-font-weight: normal;\">hotrinhdinhyenthuong.vn</strong>&rdquo; đ&acirc;y l&agrave; thực chất l&agrave; tờ b&aacute;o <strong style=\"mso-bidi-font-weight: normal;\">HỌ TRỊNH Đ&Igrave;NH Y&Ecirc;N THƯỢNG</strong> online. Tờ b&aacute;o c&oacute; nhiều chuy&ecirc;n mục như: Giới thiệu , Tin tức sự ki&ecirc;n., khuyến học, khuyến t&agrave;i. T&igrave;m về cội nguồn. Thư vi&ecirc;n., Li&ecirc;n hệ v.v.v. Trong đ&oacute; c&oacute; phần GIA PHẢ l&agrave; quan trọng nhất. Cập nhật Gia phả tr&ecirc;n Website để cho mọi người c&oacute; thế tiếp cận, thường xuy&ecirc;n v&agrave; mọi l&uacute;c, mọi nơi. Kh&aacute;c với c&aacute;c cuốn Gia phả phải v&agrave;i chục năm sau với viết tiếp hoặc bổ xung những thay đổi của d&ograve;ng họ. Gia phả tr&ecirc;n Website c&oacute; ưu điểm l&agrave; bổ xung, cập nhật kịp thời những thay đổi của d&ograve;ng họ. Mục Gia phả gồm c&oacute; Phả k&yacute; v&agrave; Phả đồ. Phả k&yacute; gồm 2 cuốn Tập I v&agrave; Tập II. Tập I l&agrave; bản gốc, Tập II do ban bi&ecirc;n tập mới lập. Phần Phả đồ được cập nhật từ đời thứ nhất đến 31/8/2023.</p>', '<p class=\"MsoNormal\" style=\"text-align: justify; text-indent: .5in;\">Sau một thời gian nỗ lực của c&aacute;c th&agrave;nh vi&ecirc;n Ban bi&ecirc;n tập c&ugrave;ng với sự phối hợp chặt chẽ với C&ocirc;ng Ty TNHH phần mền Nh&acirc;n H&ograve;a, Ban bi&ecirc;n tập đ&atilde; ho&agrave;n th&agrave;nh to&agrave;n bộ trang Website &ldquo; <strong style=\"mso-bidi-font-weight: normal;\">hotrinhdinhyenthuong.vn</strong>&rdquo; đ&acirc;y l&agrave; thực chất l&agrave; tờ b&aacute;o <strong style=\"mso-bidi-font-weight: normal;\">HỌ TRỊNH Đ&Igrave;NH Y&Ecirc;N THƯỢNG</strong> online. Tờ b&aacute;o c&oacute; nhiều chuy&ecirc;n mục như: Giới thiệu , Tin tức sự ki&ecirc;n., khuyến học, khuyến t&agrave;i. T&igrave;m về cội nguồn. Thư vi&ecirc;n., Li&ecirc;n hệ v.v.v. Trong đ&oacute; c&oacute; phần GIA PHẢ l&agrave; quan trọng nhất. Cập nhật Gia phả tr&ecirc;n Website để cho mọi người c&oacute; thế tiếp cận, thường xuy&ecirc;n v&agrave; mọi l&uacute;c, mọi nơi. Kh&aacute;c với c&aacute;c cuốn Gia phả phải v&agrave;i chục năm sau với viết tiếp hoặc bổ xung những thay đổi của d&ograve;ng họ. Gia phả tr&ecirc;n Website c&oacute; ưu điểm l&agrave; bổ xung, cập nhật kịp thời những thay đổi của d&ograve;ng họ. Mục Gia phả gồm c&oacute; Phả k&yacute; v&agrave; Phả đồ. Phả k&yacute; gồm 2 cuốn Tập I v&agrave; Tập II. Tập I l&agrave; bản gốc, Tập II do ban bi&ecirc;n tập mới lập. Phần Phả đồ được cập nhật từ đời thứ nhất đến 31/8/2023.</p>\r\n<p class=\"MsoNormal\" style=\"text-align: justify; text-indent: .5in;\">Người d&ugrave;ng điện thoại th&ocirc;ng minh hoặc m&aacute;y t&iacute;nh khi muốn v&agrave;o trang th&ocirc;ng tin chỉ cần truy cập v&agrave;o địa chỉ Web site<span style=\"mso-spacerun: yes;\">&nbsp; </span><strong style=\"mso-bidi-font-weight: normal;\">hotrinhdinhyenthuong.vn</strong></p>\r\n<p class=\"MsoNormal\" style=\"text-align: justify; text-indent: .5in;\">Muốn xem th&ocirc;n tin một th&agrave;nh vi&ecirc;n n&agrave;o đ&oacute; trong họ c&oacute; 2 c&aacute;ch. C&aacute;ch 1 phần phả k&yacute;, c&aacute;ch 2 mở phả đồ rồi k&iacute;ch chuột v&agrave;o người cần xem hoặc ( trạm tay v&agrave;o người đ&oacute; nếu d&ugrave;ng điện thoại) th&ocirc;ng tin người cần xem sẽ hiện ra.</p>\r\n<p class=\"MsoNormal\" style=\"text-align: justify; text-indent: .5in;\">Để tờ b&aacute;o th&ecirc;m phong ph&uacute; Ban bi&ecirc;n tập đề nghị mọi th&agrave;nh vi&ecirc;n trong họ t&iacute;ch cực viết tin b&agrave;i phản ảnh c&aacute;c hoạt động của Họ, của L&agrave;ng, X&atilde; gửi về cho Ban bi&ecirc;n tập theo địa chỉ, điện thoại ghi ở phần cuối của giao diện trang Website. Ban bi&ecirc;n tập sẽ nghi&ecirc;n cứu chọn lọc để cho đăng.</p>\r\n<p class=\"MsoNormal\" style=\"text-align: justify; text-indent: .5in;\">Mọi &yacute; kiến cần trao đổi với Ban bi&ecirc;n tập mọi người d&ugrave;ng phần <strong style=\"mso-bidi-font-weight: normal;\">&ldquo;Li&ecirc;n hệ&rdquo; </strong>tr&ecirc;n trang Website Ban bi&ecirc;n tập sẽ biết để phả hồi.</p>\r\n<p class=\"MsoNormal\" style=\"text-align: justify; text-indent: .5in;\">Ban bi&ecirc;n tập tr&acirc;n th&agrave;nh cảm ơn c&aacute;c cụ, c&aacute;c &ocirc;ng, c&aacute;c b&agrave; v&agrave; tất cả c&aacute;c th&agrave;nh vi&ecirc;n trong họ, c&aacute;c nh&agrave; t&agrave;i trợ đ&atilde; hỗ trợ, động vi&ecirc;n, gi&uacute;p đỡ để ch&uacute;ng t&ocirc;i ho&agrave;n th&agrave;nh trang Wesite n&agrave;y. Ban bi&ecirc;n tập mong mọi người tiếp tục tham gia g&oacute;p &yacute; để trang Website ng&agrave;y c&agrave;ng được ho&agrave;n thiện hơn.</p>\r\n<p class=\"MsoNormal\" style=\"text-align: center;\">Xin tr&acirc;n trọng cảm ơn.</p>\r\n<p style=\"text-align: right;\"><strong style=\"mso-bidi-font-weight: normal;\"><span style=\"font-size: 14.0pt; mso-bidi-font-size: 11.0pt; font-family: \'Times New Roman\',\'serif\'; mso-fareast-font-family: Calibri; mso-fareast-theme-font: minor-latin; mso-bidi-theme-font: minor-bidi; mso-ansi-language: EN-US; mso-fareast-language: EN-US; mso-bidi-language: AR-SA;\">BAN BI&Ecirc;N TẬP</span></strong></p>', 'Hướng dẫn sử dụng', NULL, NULL, 'huong dan su dung', 'vi');
