INSERT INTO `articles_content` VALUES(63, 58, 'Người đẹp Ninh Bình giành giải thưởng tại Cuộc thi Hoa hậu Du lịch Việt Nam toàn cầu 2024', '<p>Chung kết Cuộc thi Hoa hậu Du lịch Việt Nam to&agrave;n cầu 2024 diễn ra tại th&agrave;nh phố Hải Ph&ograve;ng tối 21/9. Ninh B&igrave;nh c&oacute; th&iacute; sinh Trịnh Thị Trung Thu (th&agrave;nh phố Tam Điệp) lọt v&agrave;o Top 10 v&agrave; được trao giải thưởng \"Người đẹp trang phục Bản sắc Việt\" cho th&iacute; sinh c&oacute; bộ trang phục ấn tượng nhất với \"C&oacute;i m&acirc;y Ninh B&igrave;nh\".</p>', '<p style=\"text-align: center;\"><img src=\"https://cdn1506.cdn4s4.io.vn/media/tin-khuyen-hoc-khuyen-tai/trao-giai1-63f06.jpg\" alt=\"\" width=\"1024\" height=\"694\"></p>\r\n<div class=\"entry-media\">\r\n<p class=\"image-caption\" style=\"text-align: center;\"><em>Ban tổ chức trao giải \"Người đẹp trang phục Bản sắc Việt\" cho Trịnh Thị Trung Thu. Ảnh: NVCC</em></p>\r\n</div>\r\n<div class=\"entry-content\">\r\n<p>Trịnh Thị Trung Thu, sinh năm 2006, đến từ phường Bắc Sơn, th&agrave;nh phố Tam Điệp. C&ocirc; hiện l&agrave; t&acirc;n sinh vi&ecirc;n Đại học Kinh tế quốc d&acirc;n H&agrave; Nội; cao 1m68, số đo 3 v&ograve;ng: 81-57-89.</p>\r\n<p>Tại Cuộc thi Người đẹp Hoa Lư 2024, Trịnh Thị Trung Thu đ&atilde; đoạt giải Người đẹp Hoa Lư thứ 3, người đẹp ứng xử xuất sắc nhất.</p>\r\n<p style=\"text-align: center;\"><img src=\"https://cdn1506.cdn4s4.io.vn/media/tin-khuyen-hoc-khuyen-tai/thu2-a5e6d.jpg\" alt=\"\" width=\"1024\" height=\"1534\"></p>\r\n<figure class=\"image\" style=\"text-align: center;\">\r\n<figcaption><em>Trịnh Thị Trung Thu tại phần thi trang phục bản sắc Việt. Ảnh: NVCC</em></figcaption>\r\n</figure>\r\n<p>Tại Chung kết Cuộc thi Hoa hậu Du lịch Việt Nam to&agrave;n cầu 2024, với bộ trang phục \"C&oacute;i m&acirc;y Ninh B&igrave;nh\", Trung Thu mong muốn lan tỏa, quảng b&aacute; nghề l&agrave;m c&oacute;i truyền thống của Kim Sơn, Ninh B&igrave;nh được h&igrave;nh th&agrave;nh từ h&agrave;ng trăm năm trước, trải qua bao thăng trầm của lịch sử, nghề c&oacute;i ng&agrave;y c&agrave;ng khẳng định vị thế của m&igrave;nh v&agrave; được g&igrave;n giữ, ph&aacute;t triển cho đến ng&agrave;y nay. Nghề c&oacute;i Kim Sơn vừa được Bộ Văn h&oacute;a, Thể thao v&agrave; Du lịch đưa v&agrave;o danh mục Di sản văn h&oacute;a phi vật thể Quốc gia.</p>\r\n<p style=\"text-align: center;\"><strong>Nguồn: baoninhbinh.org.vn</strong></p>\r\n<p style=\"text-align: right;\"><em><strong>Trịnh Thị Trung Thu l&agrave; con g&aacute;i &ocirc;ng Trịnh Đ&igrave;nh Thuận (T&ocirc;) đời thứ 10, ch 3</strong></em></p>\r\n</div>', 'Người đẹp Ninh Bình giành giải thưởng tại Cuộc thi Hoa hậu Du lịch Việt Nam toàn cầu 2024', NULL, NULL, 'nguoi dep ninh binh gianh giai thuong tai cuoc thi hoa hau du lich viet nam toan cau 2024', 'vi');
