INSERT INTO `genealogies` VALUES(653, 'Lã Thị Chì  ', 'Diệu Kim', 0, 0, 'female', '/media/bÀ lÃ thỊ chÌ.jpg', 11, NULL, '<p><em style=\"mso-bidi-font-style: normal;\"><span style=\"font-size: 14.0pt; line-height: 115%; font-family: \'Times New Roman\',serif; mso-fareast-font-family: Calibri; mso-ansi-language: EN-US; mso-fareast-language: EN-US; mso-bidi-language: AR-SA;\">B&agrave; l&agrave; người đầu ti&ecirc;n l&agrave;m c&ocirc; gi&aacute;o giữ trẻ của Hợp T&aacute;c x&atilde;&nbsp; Y&ecirc;n Li&ecirc;u Thượng: tiền th&acirc;n của Trường Mầm nom X&atilde; Kh&aacute;nh Thịnh ng&agrave;y nay. Với cống hiến v&agrave; c&ocirc;ng lao đ&oacute;ng g&oacute;p cho ngh&agrave;nh gi&aacute;o dục B&agrave; được Bộ trưởng Bộ Gi&aacute;o dục&nbsp; tặng Kỷ Niệm Chương &ldquo; V&igrave; Sự nghiệp Gi&aacute;o Dục&rdquo; </span></em></p>', 2, 407, NULL, '|1|2|4|9|17|25|40|117|210|407|', NULL, 1199725200, NULL, '1935', '8/1/2008 ( 1/12/ 2007 Âm lịch)', 'Nghĩa trang Đồng Vỡ', NULL, NULL, 0, 0, 'la thi chi | dieu kim');
INSERT INTO `genealogies` VALUES(654, 'Trịnh Đình Toan', NULL, 5, 1, 'male', NULL, 11, 'Là con trai thứ:    Nhất Của ông:      Trịnh Đình Thử', '<p class=\"MsoNormal\" style=\"text-align: justify; line-height: 18.0pt; tab-stops: dotted 16.0cm; margin: 3.0pt 0cm 3.0pt 0cm;\">Năm 1975 tham gia Bộ đội ( Qu&acirc;n Chủng Hải Qu&acirc;n) Tham gia chiến dịch Hồ Ch&iacute; Minh Giải ph&oacute;ng S&agrave;i G&ograve;n. H&ograve;a b&igrave;nh được Qu&acirc;n đội cho thi v&agrave;o Học viện KTQS v&agrave; gửi đi học tại Đại Học X&acirc;y Dựng H&agrave; Nội, ra trường c&ocirc;ng t&aacute;c tại Binh Đo&agrave;n 11- BQP cấp bậc Qu&acirc;n H&agrave;m cao nhất: Đại T&aacute;. Trong thời gian c&ocirc;ng t&aacute;c đ&atilde; gữi c&aacute;c chức vụ: Đảng ủy vi&ecirc;n Đảng ủy Binh Đo&agrave;n 11, Gi&aacute;m đốc C&ocirc;ng Ty Tư vấn 491, Gi&aacute;m đốc C&ocirc;ng ty XL 665. <span style=\"mso-spacerun: yes;\">&nbsp;</span>Từ năm 1995 &ndash; 2000 &Ocirc;ng được Bộ Quốc Ph&ograve;ng cử sang L&agrave;o l&agrave;m Gi&aacute;m đốc C&ocirc;ng Ty ( ACSCO) - li&ecirc;n doanh giữa Bộ Quốc Ph&ograve;ng hai nước , v&agrave; nhiều chức vụ quan trọng kh&aacute;c. &Ocirc;ng được Đảng, Nh&agrave; nước v&agrave; Qu&acirc;n đội Tặng thưởng Hu&acirc;n Chương Chiến c&ocirc;ng Hạng Nhất, Hu&acirc;n Chương Bảo vệ Tổ Quốc, Nh&agrave; nước L&agrave;o Tặng Hu&acirc;n Chương ITXALA . Ba Hu&acirc;n Chương Chiến Sỹ Vẻ vang, Huy Chương Qu&acirc;n kỳ Quyết thắng. Kỷ niệm chương ngh&agrave;nh X&acirc;y dựng, T&agrave;i ch&iacute;nh, Hậu cần v&agrave; nhiều phần thưởng cao qu&iacute; kh&aacute;c. &Ocirc;ng được nghỉ hưu năm 2015.</p>', 3, 407, 1, '|1|2|4|9|17|25|40|117|210|407|', -392886000, NULL, NULL, '21/07/1957', NULL, NULL, 1, NULL, 1, 0, 'trinh dinh toan');
